import React, { useEffect, useState } from 'react'
import './ViewEmployee.css';
import Axios from 'axios';
function ViewEmployee() {

        // Search State
        const [search,Setsearch] = useState('');
        const [searchdb,Setsearchdb] = useState('');
        const [searchdiv,Setsearchdiv] = useState(false);
        const [content,Setcontent] = useState('');
        // end of state

        
        // get from db
        useEffect(()=>{
                fetch("http://localhost:5000/api/employee/search",{
                        method:"GET",
                }).then((res)=>res.json()).then((data)=>{
                        Setsearchdb(data);
                        console.log(data);
                })
        },[]);
        // get from db end


        const SearchHandler =(e)=>{
                const list = searchdb.map((i)=>{
                        if(i.phonenumber == search) {
                                Setcontent()
                        }
                        else {
                                console.log('not');
                        }
                });      
        }
        return(
        <div className='ViewEmployee'>
            <div className='search-bar'>
                <div className='search-inside'>
                        <input type='text' onChange={(e)=>{Setsearch(e.target.value);Setsearchdiv(true)}}></input>
                        <span onClick={SearchHandler}>one</span>
                </div>
            </div>
            <div className='container'>
                
            </div>
            
        </div>

        );
}

export default ViewEmployee;
