                                </div>}
                    </div>
                </div>
                <div className='right-content'>
                        {stats &&<div>
                            <Stats></Stats>
                            </div>}
                        {addemployee && <div>
                                <AddEmployee></AddEmployee>
                            </div>}
                        {viewemployee && <div>
                            <ViewEmployee></ViewEmployee>
                            </div>}
                </div>
            </div>
        </div>
    );
}

export default Dashboard;
