import react, { useState } from 'react';
import './Dashboard.css';
import Stats from '../Components/Stats';
import Employee from '../Components/Employee';
function  Dashboard() {
    const [employee,Setemployee] = useState(false);
    const [customer,Setcustomer] = useState(false);
    const [stats,Setstats] = useState(false);
    const [addemployee,Setaddemployee] = useState(false);
    const [viewemployee, Setviewemployee] = useState(false);
    return (
        <div className='Dashboard'>
            <div className='content'>
                <div className='left-content'>
                    <label className='admin'>ADMIN</label>
                    <div className='left-down'>
                        <label onClick={(e)=>{Setstats(!stats); Setaddemployee(false);S}}>Dashboard</label>
                        <label onClick={(e)=>{
                            Setemployee(!employee)
                        }}>Employee</label>
                            {employee && <div className='list'>
                                <label onClick={(e)=>{Setaddemployee(!addemployee);Setstats(false)}}>Add</label>
                                <label onClick={(e=>{Setviewemployee(!viewemployee)})}>View</label>
                                </div>}
                        <label onClick={(e)=>{
                            Setcustomer(!customer)
                        }}>Customer</label>
                        {customer && <div className='list'>
                                <label>Add</label>
                                <label>View</label>
                                </div>}
                    </div>
                </div>
                <div className='right-content'>
                        {stats &&<div>
                            <Stats></Stats>
                            </div>}
                            {addemployee && <div>
                                <Employee></Employee>
                                </div>}
                </div>
            </div>
        </div>
    );
}

export default Dashboard;
