// import emailjs from '@emailjs/browser';
import './popup.css';
const Popup = (props) => {
    const {trigger,setTrigger,details} = props;
  const [issubmit, setIsSubmit] = useState(false);
//  const [Empdetails,SetEmpdetails] = useState(details);
console.log(details.email)
  const [email,Setemail] = useState('');
  const [name,Setname] = useState('');
  const [password,Setpassword] = useState('');
  const [phonenumber,Setphonenumber] = useState('');
  const [Address,SetAddress] = useState('');
    Setemail(details.email);
    Setname(details.name);
    Setpassword(details.password)
  const SubmitHandler = async (e) => {
    e.preventDefault();
//    try {
//     const {data} = await Axios.post('http://localhost:5000/api/employee/add',{
//         email,
//         name,
//         password,
//         phonenumber,
//         Address
//     });
    // console.log(data)
    // ctxDispatch({type:'EMLPLOYEE_ADD', payload : data});
    // localStorage.setItem('employee_add',JSON.stringify(data)); 
//    }
//    catch(error) {
//         console.log(error);
//    }
};
  return trigger ? (
    <div className="popup">
      <div className="popup-inner">
        <button
          className="close-button"
          onClick={() => setTrigger(false)}
        >
            kkk
          <i className="fa-solid fa-circle-xmark"></i>
        </button>
        {!issubmit ? (
          <>
             <form onSubmit={SubmitHandler}>
<div className='details-top'>
                    <label>Email Address:<input type='email' onChange={(e)=>{Setemail(email)}}></input></label>
                    <label>Name:<input type='text' onChange={(e)=>{Setname(name)}}></input></label>
                    <label>password:<input type='password' onChange={(e)=>{Setpassword(password)}}></input></label>
                    </div>
                    <div className='details-down'>
                    <label>Phonenumber:<input type='number' onChange={(e)=>{Setphonenumber(phonenumber)}}></input></label>
                    <label>Address:<input type='text' className='Address' onChange={(e)=>{SetAddress(Address)}}></input></label>
                    <input className='button'type='submit' value='submit'></input>
                    </div>                     
                  </form>
          </>
        ) : (
          <div>Thank you Our Team Reach you Soon.</div>
        )}
      </div>
    </div>
  ) : (
    ''
  );
};

export default Popup;
